                            AND      c.validita_fine IS NULL
                            /*siac-5297
                            AND      date_trunc('day',datainiziovalclass)>=date_trunc('day',c.validita_inizio)
                            AND      (
                                              date_trunc('day',datafinevalclass)<=date_trunc('day',c.validita_fine)
                                     OR       c.validita_fine IS NULL)
                                     */
