 raise notice 'p_tefa_trib_comune_anno=%',p_tefa_trib_comune_anno::varchar;
 
 annoPrecPrec:=p_tefa_trib_comune_anno-2;
 annoPrec:=p_tefa_trib_comune_anno-1;

