        fase.ente_proprietario_id			=	anno_eserc.ente_proprietario_id	and*/
        tipo_periodo.periodo_tipo_id		=	anno_eserc.periodo_tipo_id		and
        r_fase.bil_id						=	bilancio.bil_id					AND
