		ON UPDATE NO ACTION,
	CONSTRAINT siac_t_subdoc_siac_t_subdoc_sospensione FOREIGN KEY (subdoc_id)
		REFERENCES siac.siac_t_subdoc(subdoc_id)
		ON DELETE NO ACTION
