    bil_anno_classe,
    anno_impegno_classe,
    num_impegno_classe,
    cod_movgest_ts_classe,
    desc_movgest_ts_classe,
    tipo_movgest_ts_classe,
    numero_modifica,
    desc_modifica,
    stato_modifica,
