 CREATE OR REPLACE VIEW siac.siac_v_dwh_mod_impegno_sogg (
    ente_proprietario_id,
    bil_anno_sogg,
    anno_impegno_sogg,
    num_impegno_sogg,
    cod_movgest_ts_sogg,
    desc_movgest_ts_sogg,
