    cf_estero_old,
    partita_iva_old,
    cod_soggeto_new,
    desc_soggetto_new,
    cf_new,
    cf_estero_new,
    partita_iva_new,
    anno_atto_amministrativo,
    num_atto_amministrativo,
    cod_tipo_atto_amministrativo,
    cod_sac,
    desc_sac,
    tipo_sac,
    desc_stato_modifica,
    tipo_modifica)
AS
 WITH a AS (
SELECT tm.ente_proprietario_id, tm.mod_num, tm.mod_desc,
            dms.mod_stato_code, dmt.mod_tipo_code, dmt.mod_tipo_desc, tp.anno,
