,
transue as (
SELECT 
a.liq_id ,b.classif_code v_codice_transazione_spesa_ue, b.classif_desc v_descrizione_transazione_spesa_ue
 from siac_r_liquidazione_class a, siac_t_class b, siac_d_class_tipo c
