               c.data_cancellazione is null and
               d.data_cancellazione is null and
               e.data_cancellazione is null and
               e.ambito_code = 'AMBITO_GSA' and
              now() between a.validita_inizio and COALESCE(a.validita_fine,now())
          loop
 			
