	  ambito_id
	 )
     (select
            1,
            (case when movEpDetSegno=SEGNO_AVERE_MOV then SEGNO_DARE_MOV else SEGNO_AVERE_MOV end),
            movEpDetImporto,
	        movepId,
            fasetipo.pdce_conto_ep_id,
	        dataInizioVal,
