RETURNS record AS
$body$
DECLARE

	strMessaggio VARCHAR(1500):='';
	strMessaggioFinale VARCHAR(1500):='';
	codResult integer:=null;

    ELAB_MIF_ESITO_IN       CONSTANT  varchar :='IN';


