    -- sulla mif_t_flusso_elaborato
    strMessaggio:='Verifica esistenza elaborazioni in corso [mif_t_flusso_elaborato].';
	codResult:=null;
    select distinct 1 into codResult
    from mif_t_flusso_elaborato elab
