                                                                  dataelaborazione TIMESTAMP,
                                                                  OUT fasebilelabidret   INTEGER,
                                                                  OUT codicerisultato    INTEGER,
                                                                  OUT messaggiorisultato VARCHAR )
returns RECORD
AS
  $body$
  DECLARE
    strmessaggio       VARCHAR(1500):='';
    strmessaggiofinale VARCHAR(1500):='';
    -- tipo periodo annuale
