CREATE OR REPLACE FUNCTION siac.fnc_siac_cons_entita_reversale_from_soggetto_total (
  _uid_soggetto integer,
  _annoEsercizio varchar
)
RETURNS bigint AS
$body$
DECLARE
	total bigint;
BEGIN

	SELECT coalesce(count(*),0)
	into total
	from (
		with ord_join_outer as (
			with ord_join as (
				with ordinativo as (
					select
						a.ord_id as uid,
						a.ord_numero,
						a.ord_desc,
						a.ord_emissione_data,
						e.ord_stato_desc,
